                {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
            </span>
            <span className="theme-text">
                Switch to {theme === 'light' ? 'Dark' : 'Light'} Theme
            </span>
        </button>
    );
};

export default ThemeToggle;
